// Normalize positions - always output 1 item even if empty
const positions = $input.all().map(i => i.json);
return [{ json: { positions: positions } }];

// Build portfolio from account + normalized positions
const account = $('Fetch PPO_LSTM Account').first().json;
const posData = $input.first().json;
const positions = posData.positions || [];
return {
  json: {
    portfolio_ppo_lstm: {
      cash: parseFloat(account.cash || '0'),
      positions: positions.map(p => ({
        symbol: p.symbol,
        qty: parseFloat(p.qty || '0'),
        market_value: parseFloat(p.market_value || '0')
      }))
    }
  }
};

// Normalize positions - always output 1 item even if empty
const positions = $input.all().map(i => i.json);
return [{ json: { positions: positions } }];

// Build portfolio from account + normalized positions
const account = $('Fetch SAC_PatchTST Account').first().json;
const posData = $input.first().json;
const positions = posData.positions || [];
return {
  json: {
    portfolio_sac_patchtst: {
      cash: parseFloat(account.cash || '0'),
      positions: positions.map(p => ({
        symbol: p.symbol,
        qty: parseFloat(p.qty || '0'),
        market_value: parseFloat(p.market_value || '0')
      }))
    }
  }
};

// Normalize positions - always output 1 item even if empty
const positions = $input.all().map(i => i.json);
return [{ json: { positions: positions } }];

// Build portfolio from account + normalized positions
const account = $('Fetch HRP Account').first().json;
const posData = $input.first().json;
const positions = posData.positions || [];
return {
  json: {
    portfolio_hrp: {
      cash: parseFloat(account.cash || '0'),
      positions: positions.map(p => ({
        symbol: p.symbol,
        qty: parseFloat(p.qty || '0'),
        market_value: parseFloat(p.market_value || '0')
      }))
    }
  }
};

const N = 20;
const stocks = $input.first().json.stocks || [];
const symbols = stocks.slice(0, N).map(s => s.symbol);
const asOfDate = new Intl.DateTimeFormat('en-CA', { timeZone: 'Asia/Kolkata' }).format(new Date());
const runId = `paper:${asOfDate}`;
const mockPortfolio = { cash: 10000, positions: [] };
return { json: { symbols, as_of_date: asOfDate, run_id: runId, stock_count: symbols.length, mock_portfolio: mockPortfolio } };

const input = $input.first().json;
const asOfDate = input.as_of_date || new Date().toISOString().split('T')[0];
const runId = input.run_id || `paper:${asOfDate}`;
const mockPortfolio = { cash: 10000, positions: [] };

// Get real portfolios from earlier nodes
let portfolio_ppo_lstm = mockPortfolio;
let portfolio_sac_patchtst = mockPortfolio;
let portfolio_hrp = mockPortfolio;

try { portfolio_ppo_lstm = $('Build PPO_LSTM Portfolio').first().json.portfolio_ppo_lstm || mockPortfolio; } catch(e) {}
try { portfolio_sac_patchtst = $('Build SAC_PatchTST Portfolio').first().json.portfolio_sac_patchtst || mockPortfolio; } catch(e) {}
try { portfolio_hrp = $('Build HRP Portfolio').first().json.portfolio_hrp || mockPortfolio; } catch(e) {}

return {
  json: {
    ...input,
    as_of_date: asOfDate,
    run_id: runId,
    attempt: 1,
    mock_portfolio: mockPortfolio,
    portfolio_ppo_lstm,
    portfolio_sac_patchtst,
    portfolio_hrp
  }
};

// Generate orders for PPO+LSTM
const phase2 = $('Prepare Phase 2').first().json;
const allocation = $input.first().json;
return {
  json: {
    target_weights: allocation.target_weights || {},
    portfolio: phase2.portfolio_ppo_lstm,
    run_id: phase2.run_id,
    attempt: phase2.attempt,
    algorithm: 'ppo_lstm',
    allocation_result: allocation
  }
};

// Generate orders for SAC+PatchTST
const phase2 = $('Prepare Phase 2').first().json;
const allocation = $input.first().json;
return {
  json: {
    target_weights: allocation.target_weights || {},
    portfolio: phase2.portfolio_sac_patchtst,
    run_id: phase2.run_id,
    attempt: phase2.attempt,
    algorithm: 'sac_patchtst',
    allocation_result: allocation
  }
};

// Generate orders for HRP - convert percentage weights to decimal
const phase2 = $('Prepare Phase 2').first().json;
const allocation = $input.first().json;
const pctWeights = allocation.percentage_weights || {};
const decWeights = {};
for (const [sym, pct] of Object.entries(pctWeights)) {
  decWeights[sym] = pct / 100;
}
return {
  json: {
    target_weights: decWeights,
    portfolio: phase2.portfolio_hrp,
    run_id: phase2.run_id,
    attempt: phase2.attempt,
    algorithm: 'hrp',
    allocation_result: allocation
  }
};

// Extract orders and submit each to Alpaca
const orders = $input.first().json.orders || [];
return orders.map(order => ({ json: { ...order, account: 'ppo_lstm' } }));

// Collect order results for PPO_LSTM
const results = $input.all().map(i => i.json);
const successful = results.filter(r => r.id && !r.error).length;
const failed = results.filter(r => r.error || !r.id).length;
return { json: { account: 'ppo_lstm', orders_submitted: successful, orders_failed: failed, results } };

const results = $input.all().map(i => i.json);
const successful = results.filter(r => r.id && !r.error).length;
const failed = results.filter(r => r.error || !r.id).length;
return { json: { account: 'sac_patchtst', orders_submitted: successful, orders_failed: failed, results } };

const results = $input.all().map(i => i.json);
const successful = results.filter(r => r.id && !r.error).length;
const failed = results.filter(r => r.error || !r.id).length;
return { json: { account: 'hrp', orders_submitted: successful, orders_failed: failed, results } };

// Collect all order execution results
let orderResults = { ppo_lstm: { orders_submitted: 0, orders_failed: 0 }, sac_patchtst: { orders_submitted: 0, orders_failed: 0 }, hrp: { orders_submitted: 0, orders_failed: 0 } };
try { const r = $('Collect Results PPO_LSTM').first().json; orderResults.ppo_lstm = r; } catch(e) {}
try { const r = $('Collect Results SAC_PatchTST').first().json; orderResults.sac_patchtst = r; } catch(e) {}
try { const r = $('Collect Results HRP').first().json; orderResults.hrp = r; } catch(e) {}

const phase2Merged = $input.first().json;
const phase1Data = $('Prepare Phase 2').first().json;

let lstmPredictions = [];
let lstmModelVersion = 'unknown';
let targetWeekStart = 'N/A';
let targetWeekEnd = 'N/A';
try { const d = $('POST LSTM Forecast').first().json; lstmPredictions = d.predictions || []; lstmModelVersion = d.model_version || 'unknown'; targetWeekStart = d.target_week_start || 'N/A'; targetWeekEnd = d.target_week_end || 'N/A'; } catch (e) {}

let patchtstPredictions = [];
let patchtstModelVersion = 'unknown';
let patchtstSignalsUsed = [];
try { const d = $('POST PatchTST Forecast').first().json; patchtstPredictions = d.predictions || []; patchtstModelVersion = d.model_version || 'unknown'; patchtstSignalsUsed = d.signals_used || []; } catch (e) {}

let newsSentiments = [];
try { newsSentiments = $('POST News Sentiment').first().json.per_symbol || []; } catch (e) {}

let fundamentalsPerSymbol = [];
try { fundamentalsPerSymbol = $('POST Fundamentals').first().json.per_symbol || []; } catch (e) {}

const hrpWeights = phase2Merged.percentage_weights || {};
const hrpSymbolsUsed = phase2Merged.symbols_used || 0;
const hrpSymbolsExcluded = phase2Merged.symbols_excluded || [];
const asOfDate = phase2Merged.as_of_date || phase1Data.as_of_date || 'N/A';

let sacLstmWeights = {}, sacLstmTurnover = 0, sacLstmVersion = 'unknown';
let sacPatchtstWeights = {}, sacPatchtstTurnover = 0, sacPatchtstVersion = 'unknown';
let ppoLstmWeights = {}, ppoLstmTurnover = 0, ppoLstmVersion = 'unknown';
let ppoPatchtstWeights = {}, ppoPatchtstTurnover = 0, ppoPatchtstVersion = 'unknown';

try { const d = $('POST SAC+LSTM').first().json; sacLstmWeights = d.target_weights || {}; sacLstmTurnover = d.turnover || 0; sacLstmVersion = d.model_version || 'unknown'; } catch (e) {}
try { const d = $('POST SAC+PatchTST').first().json; sacPatchtstWeights = d.target_weights || {}; sacPatchtstTurnover = d.turnover || 0; sacPatchtstVersion = d.model_version || 'unknown'; } catch (e) {}
try { const d = $('POST PPO+LSTM').first().json; ppoLstmWeights = d.target_weights || {}; ppoLstmTurnover = d.turnover || 0; ppoLstmVersion = d.model_version || 'unknown'; } catch (e) {}
try { const d = $('POST PPO+PatchTST').first().json; ppoPatchtstWeights = d.target_weights || {}; ppoPatchtstTurnover = d.turnover || 0; ppoPatchtstVersion = d.model_version || 'unknown'; } catch (e) {}
try { const d = $('POST HRP Allocation').first().json; if (Object.keys(hrpWeights).length === 0) Object.assign(hrpWeights, d.percentage_weights || {}); } catch (e) {}

const newsMap = {};
for (const ns of newsSentiments) { if (ns.sentiment_score !== undefined) newsMap[ns.symbol] = ns; }
const fundamentalsMap = {};
for (const f of fundamentalsPerSymbol) { if (f.ratios) fundamentalsMap[f.symbol] = f.ratios; }

const formatWeight = (w) => w !== undefined ? (w * 100).toFixed(1) + '%' : 'N/A';
const formatHrpWeight = (w) => w !== undefined ? w.toFixed(1) + '%' : 'N/A';
const getTop5 = (weights) => Object.entries(weights).filter(([k, v]) => k !== 'CASH').sort((a, b) => b[1] - a[1]).slice(0, 5).map(([sym, wt]) => `${sym}: ${typeof wt === 'number' && wt < 1 ? formatWeight(wt) : formatHrpWeight(wt)}`).join(', ');

const prompt = `You are a financial analyst. Provide a brief JSON analysis with these 10 fields:

TOP ALLOCATIONS:
HRP: ${getTop5(hrpWeights) || 'N/A'}
SAC+LSTM: ${getTop5(sacLstmWeights) || 'N/A'}
SAC+PatchTST: ${getTop5(sacPatchtstWeights) || 'N/A'}
PPO+LSTM: ${getTop5(ppoLstmWeights) || 'N/A'}
PPO+PatchTST: ${getTop5(ppoPatchtstWeights) || 'N/A'}

Respond with JSON only:
{"para_1_overall_summary":"<3 lines>","para_2_sac_lstm":"<3 lines>","para_3_sac_patchtst":"<3 lines>","para_4_ppo_lstm":"<3 lines>","para_5_ppo_patchtst":"<3 lines>","para_6_hrp_summary":"<3 lines>","para_7_patchtst_forecast":"<3 lines>","para_8_lstm_forecast":"<3 lines>","para_9_news_sentiment":"<3 lines>","para_10_fundamentals":"<3 lines>"}`;

return {
  json: {
    lstm: { predictions: lstmPredictions, model_version: lstmModelVersion, target_week_start: targetWeekStart, target_week_end: targetWeekEnd },
    patchtst: { predictions: patchtstPredictions, model_version: patchtstModelVersion, signals_used: patchtstSignalsUsed },
    news: { per_symbol: newsSentiments },
    fundamentals: { per_symbol: fundamentalsPerSymbol },
    hrp: { percentage_weights: hrpWeights, symbols_used: hrpSymbolsUsed, symbols_excluded: hrpSymbolsExcluded, as_of_date: asOfDate },
    sac_lstm: { target_weights: sacLstmWeights, turnover: sacLstmTurnover, model_version: sacLstmVersion },
    sac_patchtst: { target_weights: sacPatchtstWeights, turnover: sacPatchtstTurnover, model_version: sacPatchtstVersion },
    ppo_lstm: { target_weights: ppoLstmWeights, turnover: ppoLstmTurnover, model_version: ppoLstmVersion },
    ppo_patchtst: { target_weights: ppoPatchtstWeights, turnover: ppoPatchtstTurnover, model_version: ppoPatchtstVersion },
    order_results: orderResults,
    openai_prompt: prompt
  }
};

const input = $input.first().json;
const prevData = $('Prepare OpenAI Prompt').first().json;
const orderResults = prevData.order_results || {};

let aiSummary = {};
try { const content = input.message?.content || '{}'; aiSummary = JSON.parse(content); } catch (e) { aiSummary = { para_1_overall_summary: 'Unable to generate AI summary' }; }

const lstmData = prevData.lstm || {};
const hrpData = prevData.hrp || {};
const targetWeekStart = lstmData.target_week_start || 'N/A';
const targetWeekEnd = lstmData.target_week_end || 'N/A';
const asOfDate = hrpData.as_of_date || 'N/A';

// Order execution summary
const orderSection = `
<div style="margin-bottom: 24px; padding: 16px; background: #f0fdf4; border: 2px solid #22c55e; border-radius: 8px;">
  <h3 style="color: #166534; margin-top: 0;">📈 Order Execution Summary</h3>
  <table style="width: 100%; border-collapse: collapse;">
    <tr style="background: #dcfce7;"><th style="padding: 8px; text-align: left;">Account</th><th style="padding: 8px;">Submitted</th><th style="padding: 8px;">Failed</th></tr>
    <tr><td style="padding: 8px;">PPO_LSTM</td><td style="padding: 8px; text-align: center;">${orderResults.ppo_lstm?.orders_submitted || 0}</td><td style="padding: 8px; text-align: center;">${orderResults.ppo_lstm?.orders_failed || 0}</td></tr>
    <tr><td style="padding: 8px;">SAC_PatchTST</td><td style="padding: 8px; text-align: center;">${orderResults.sac_patchtst?.orders_submitted || 0}</td><td style="padding: 8px; text-align: center;">${orderResults.sac_patchtst?.orders_failed || 0}</td></tr>
    <tr><td style="padding: 8px;">HRP</td><td style="padding: 8px; text-align: center;">${orderResults.hrp?.orders_submitted || 0}</td><td style="padding: 8px; text-align: center;">${orderResults.hrp?.orders_failed || 0}</td></tr>
  </table>
</div>`;

const aiSection = `<div style="margin-bottom: 24px; padding: 20px; background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); border-radius: 12px; color: white;"><h2 style="margin-top: 0;">AI Analysis</h2><p>${aiSummary.para_1_overall_summary || 'N/A'}</p></div>`;

const emailBody = `<div style="font-family: sans-serif; max-width: 900px; margin: 0 auto; padding: 20px;"><h1>Weekly Portfolio Analysis</h1><div style="background: #f3f4f6; padding: 16px; border-radius: 8px; margin-bottom: 24px;"><strong>Target Week:</strong> ${targetWeekStart} → ${targetWeekEnd} | <strong>As-of:</strong> ${asOfDate}</div>${orderSection}${aiSection}<div style="margin-top: 32px; font-size: 12px; color: #6b7280;"><em>Generated by LearnFinance-2025 | Not financial advice</em></div></div>`;

return { json: { emailSubject: `Weekly Portfolio Analysis (${targetWeekStart} → ${targetWeekEnd})`, emailBody, orderResults } };
